<?php
$string = "";

$stringArrays = [];
$strings = [];
$split = str_split($string);
$count = count($split);
$value = [];
for ($i = 0; $i <= $count - 4; $i++) {
    $stringArrays[] = [
        $split[$i],
        $split[$i + 1],
        $split[$i + 2],
        $split[$i + 3],
    ];
    if (empty($stringArrays[count($stringArrays) - 1][3])) {
        array_pop($stringArrays);
    }
}

foreach ($stringArrays as $index => $stringArray) {
    if (count(array_unique($stringArray)) === 4) {
        $value = $index + 4;
        break;
    }
}
print_r($value);